#include<iostream>using namespace std;int Count(long int n, long int* arr){	int P = 1000000007;	int temp = 0;	if (n < 0) { return 0; }	if (n == 0) { return 1; }	if (arr[n] != -1)	{		return arr[n];	}	for (int i = 1; i <= 6; i++) {		temp = (temp + Count(n - i, arr)) % P;	}	return arr[n] = temp;}int main(){	long int n = 0;	cin >> n;	long int* arr = new long int[n + 1];	fill_n(arr, n + 1, -1);	arr[0] = 1;	cout << Count(n, arr);	return 0;}